"use strict";
// Basic Data Types
// Primitives
// String
let userName = "Nazmul Hassan"; // implicit type [string]
let user = "Nazmul Hassan"; // explicit type [string]
// Number
let salary = 500000;
// Boolean
let isActive = true;
// undefined
let valid = undefined;
// null
let nonExistent = null;
// Any
let x; // implicit
let y; //explicit
x = 12;
x = true;
y = undefined;
y = null;
// We should not use implicit/explicit any
// Non-Primitives
// Array
let friends = ["Monica", "Joey"];
let salaries = [2000, 5000, 3000];
// Tuple
// Special type of array
let coordinates;
coordinates = ["5", 6, false];
